import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";

const pizzaData = [
  // {
  //   name: "Focaccia",
  //   ingredients: "Bread with italian olive oil and rosemary",
  //   price: 6,
  //   photoName: "pizzas/focaccia.jpg",
  //   soldOut: false,
  // },
  // {
  //   name: "Pizza Margherita",
  //   ingredients: "Tomato and mozarella",
  //   price: 10,
  //   photoName: "pizzas/margherita.jpg",
  //   soldOut: false,
  // },
  // {
  //   name: "Pizza Spinaci",
  //   ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
  //   price: 12,
  //   photoName: "pizzas/spinaci.jpg",
  //   soldOut: false,
  // },
  // {
  //   name: "Pizza Funghi",
  //   ingredients: "Tomato, mozarella, mushrooms, and onion",
  //   price: 12,
  //   photoName: "pizzas/funghi.jpg",
  //   soldOut: false,
  // },
  // {
  //   name: "Pizza Salamino",
  //   ingredients: "Tomato, mozarella, and pepperoni",
  //   price: 15,
  //   photoName: "pizzas/salamino.jpg",
  //   soldOut: true,
  // },
  // {
  //   name: "Pizza Prosciutto",
  //   ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
  //   price: 18,
  //   photoName: "pizzas/prosciutto.jpg",
  //   soldOut: false,
  // },
];

function App() {
  return (
    <div className="container">
      <Header />
      <Menu />
      <Footer />
    </div>
  );
}

function Header() {
  return (
    <header className="header">
      <h1>Fast React Pizza Co.</h1>
    </header>
  );
}
function Menu() {
  const pizzasNum = [];
  const pizzas = pizzasNum.length;
  return (
    <main className="menu">
      <h2>Our Menu</h2>
      <ul className="pizzas">
        {pizzas && pizzas.map((pizza) => <Pizza pizzaObj={pizza} />)}
      </ul>
      {/* <Pizza
        name="Pizza Salamino"
        ingredients="Tomato, mozarella, and pepperoni"
        price={15}
        photoName="pizzas/salamino.jpg"
      /> */}
    </main>
  );
}

function Pizza(props) {
  return (
    <div className="pizza">
      <img src={props.pizzaObj.photoName} alt="img not found" />
      <div>
        <h3>{props.pizzaObj.name}</h3>
        <p>{props.pizzaObj.ingredients}</p>
        <p>{props.pizzaObj.price + 3}</p>
      </div>
    </div>
  );
}
function Footer() {
  const date = new Date().getHours();
  const openHour = 12;
  const closingHour = 23;
  const isOpen = date > openHour && date < closingHour;
  console.log(isOpen);

  return (
    <footer className="footer">
      <p>
        {isOpen} && We are open until {closingHour % 12}
      </p>
    </footer>
  );
  // return React.createElement("Footer", null, "we are currently open!");
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

//Before React 18
